  &reftitle.examples;



    Ejemplo de array_uintersect_assoc


<?php
$array1 = array("a" => "verde", "b" => "marrón", "c" => "azul", "rojo");
$array2 = array("a" => "VERDE", "B" => "marrón", "amarillo", "rojo");

print_r(array_uintersect_assoc($array1, $array2, "strcasecmp"));
?>


