    &style.procedural;


<?php

$superficie = cairo_image_surface_create(CAIRO_FORMAT_ARGB32, 50, 50);

$contexto = cairo_create($superficie);

var_dump(cairo_clip_extents($contexto));

?>


    &example.outputs.similar;


array(4) {
  [0]=>
  float(0)
  [1]=>
  float(0)
  [2]=>
  float(50)
  [3]=>
  float(50)
}
