  &reftitle.examples;



    Ejemplo de Memcached::append


<?php
$m = new Memcached();
$m->addServer('localhost', 11211);
$m->setOption(Memcached::OPT_COMPRESSION, false);

$m->set('foo', 'abc');
$m->append('foo', 'def');
var_dump($m->get('foo'));
?>


    &example.outputs;


string(6) "abcdef"
