refentry.refsect1:
  &reftitle.description;

&style.oop;

   boolmysqli_result::data_seek
   intoffset


&style.procedural;

   boolmysqli_data_seek
   mysqli_resultresult
   intoffset



   La función mysqli_data_seek salta a la posición
   offset de un puntero de resultado.

&reftitle.parameters;



  &mysqli.result.description;

   offset



     Posición del campo. Debe estar comprendido entre cero y el número total de filas
     menos uno (0..mysqli_num_rows - 1).

  &reftitle.examples;

   &style.oop;


<?php
/* Abrir una conexión */
$mysqli = new mysqli("localhost", "mi_usuario", "mi_contraseña", "mundo");

/* comprobar conexión */
if (mysqli_connect_errno()) {
    printf("Conexión fallida: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, CountryCode FROM City ORDER BY Name";
if ($result = $mysqli->query($query)) {

    /* saltar a la fila numero 400 */
    $result->data_seek(399);

    /* obtener fila */
    $row = $result->fetch_row();

    printf ("Ciudad: %s  Código de país: %s\n", $row[0], $row[1]);

    /* librar resultados */
    $result->close();
}

/* cerrar conexión */
$mysqli->close();
?>




   &style.procedural;


<?php
/* Abrir conexión */
$link = mysqli_connect("localhost", "mi_usuario", "mi_contraseña", "mundo");

/* comprobar conexión */
if (!$link) {
    printf("Conexión faillida: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, CountryCode FROM City ORDER BY Name";

if ($result = mysqli_query($link, $query)) {

    /* saltar a la fila número 400 */
    mysqli_data_seek($result, 399);

    /* obtener fila */
    $row = mysqli_fetch_row($result);

    printf ("Ciudad: %s  Código de país: %s\n", $row[0], $row[1]);

    /* liberar resultado */
    mysqli_free_result($result);
}

/* cerrar conexión */
mysqli_close($link);
?>


   &examples.outputs;


Ciudad: Benin City  Código de país: NGA
