refentry.refsect1:
  &reftitle.description;

&style.oop;
  arraymysqli_stmt->error_list

&style.procedural;

   arraymysqli_stmt_error_list
   mysqli_stmtstmt



   Devuelve un array de errores de la función de la sentencia invocada
   más receientemente que tuvo éxito o falló.

&reftitle.returnvalues;


 Una lista de errores, cada uno como un array asociativo
 que contiene el errno, error, y sqlstate.

  &reftitle.examples;

   &style.oop;


<?php
/* Abrir una conexión */
$mysqli = new mysqli("localhost", "mi_usuario", "mi_contraseña", "world");

/* comprobar la conexión */
if (mysqli_connect_errno()) {
    printf("Falló la conexión: %s\n", mysqli_connect_error());
    exit();
}

$mysqli->query("CREATE TABLE myCountry LIKE Country");
$mysqli->query("INSERT INTO myCountry SELECT * FROM Country");


$consulta = "SELECT Name, Code FROM myCountry ORDER BY Name";
if ($sentencia = $mysqli->prepare($consulta)) {

    /* eliminar la tabla */
    $mysqli->query("DROP TABLE myCountry");

    /* ejecutar la consulta */
    $sentencia->execute();

    echo "Error:\n";
    print_r($sentencia->error_list);

    /* cerrar la sentencia */
    $sentencia->close();
}

/* cerrar la conexión */
$mysqli->close();
?>




   &style.procedural;


<?php
/* Abrir una conexión */
$enlace = mysqli_connect("localhost", "mi_usuario", "mi_contraseña", "world");

/* comprobar la conexión */
if (mysqli_connect_errno()) {
    printf("Falló la conexión: %s\n", mysqli_connect_error());
    exit();
}

mysqli_query($enlace, "CREATE TABLE myCountry LIKE Country");
mysqli_query($enlace, "INSERT INTO myCountry SELECT * FROM Country");


$consulta = "SELECT Name, Code FROM myCountry ORDER BY Name";
if ($sentencia = mysqli_prepare($enlace, $consulta)) {

    /* eliminar la tabla */
    mysqli_query($enlace, "DROP TABLE myCountry");

    /* ejecutar la consulta */
    mysqli_stmt_execute($sentencia);

    echo "Error:\n";
    print_r(mysql_stmt_error_list($sentencia));

    /* cerrar la sentencia */
    mysqli_stmt_close($sentencia);
}

/* cerrar la conexión */
mysqli_close($enlace);
?>


   &examples.outputs;


Array
(
    [0] => Array
        (
            [errno] => 1146
            [sqlstate] => 42S02
            [error] => Table 'world.myCountry' doesn't exist
        )

)
