&reftitle.examples;
Ejemplo de mysqlnd_qc_get_query_trace_log
mysqlnd_qc.collect_query_trace=1
<?php /* Conectar, crear y rellenar la tabla test */ $mysqli = new mysqli("host", "usuario", "contraseña", "esquema", "puerto", "socket"); $mysqli->query("DROP TABLE IF EXISTS test"); $mysqli->query("CREATE TABLE test(id INT)"); $mysqli->query("INSERT INTO test(id) VALUES (1), (2)");
/* no almacenada en caché */ $res = $mysqli->query("SELECT id FROM test WHERE id = 1"); var_dump($res->fetch_assoc()); $res->free();
/* colocada en la caché */ $res = $mysqli->query("/*" . MYSQLND_QC_ENABLE_SWITCH . "*/" . "SELECT id FROM test WHERE id = 2"); var_dump($res->fetch_assoc()); $res->free();
/* coincidencia con la caché */ $res = $mysqli->query("/*" . MYSQLND_QC_ENABLE_SWITCH . "*/" . "SELECT id FROM test WHERE id = 2"); var_dump($res->fetch_assoc()); $res->free();
var_dump(mysqlnd_qc_get_query_trace_log()); ?>
