refentry.refsect1:
&reftitle.description;

 booltidyNode::isHtml




 Indica si el nodo es parte de un documento HTML.

  &reftitle.examples;



    Extraer el código HTML de un documento HTML


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>titulo</title>'; ?>
<# 
  /* código JSTE */
  alert('Hola Mundo'); 
#>
</head>
<body>

<?php
  // código PHP 
  echo 'hola mundo!';
?>

<%
  /* código ASP */
  response.write("Hola Mundo!")
%>

<!-- Comentarios -->
Hola Mundo
</body></html>
Fuera del HTML
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

get_nodes($tidy->html());

function get_nodes($node) {
    // Verifica si el nodo actual es del tipo requerido
    if($node->isHtml()) {
        echo "\n\n# html node #" . ++$GLOBALS['num'] . "\n";
        echo $node->value;
    }

    // Verifica si el nodo actual tiene hijos
    if($node->hasChildren()) {
        foreach($node->child as $child) {
            get_nodes($child);
        }
    }
}

?>


