refentry.refsect1:
&reftitle.description;

 boolclasskit_method_redefine
 stringclassname
 stringmethodname
 stringargs
 stringcode
 intflagsCLASSKIT_ACC_PUBLIC

&note.classkit.selfmanipulation;
&warn.experimental.func;

  &reftitle.examples;



    Exemple avec classkit_method_redefine


<?php
class Example {
    function foo() {
        return "foo!\n";
    }
}

// Création d'un objet Example
$e = new Example();

// Affichage de Example::foo() (avant la redéfinition)
echo "Avant : " . $e->foo();

// Redéfinition de la méthode 'foo'
classkit_method_redefine(
    'Example',
    'foo',
    '',
    'return "bar!\n";',
    CLASSKIT_ACC_PUBLIC
);

// Affichage de Example::foo() (après la redéfinition)
echo "Après : " . $e->foo();
?>


    &example.outputs;


Avant : foo!
Après : bar!
