refentry.refsect1:
&reftitle.parameters;




   name



     Le nom de la constante.

&reftitle.errors;


 Une alerte de niveau E_WARNING sera générée
 si la constante n'est pas définie.

  &reftitle.examples;



    Exemple avec constant


<?php

define("MAXSIZE", 100);

echo MAXSIZE;
echo constant("MAXSIZE"); // identique à la ligne précédente


interface bar {
    const test = 'foobar!';
}

class foo {
    const test = 'foobar!';
}

$const = 'test';

var_dump(constant('bar::'. $const)); // string(7) "foobar!"
var_dump(constant('foo::'. $const)); // string(7) "foobar!"

?>
