refentry.refsect1:
&reftitle.description;

 public booltidyNode::isJste




 Indique si le noeud courant est un JSTE.

  &reftitle.examples;



    Extrait du code JSTE depuis un document HTML


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>title</title>'; ?>
<# 
  /* code JSTE */
  alert('Hello World'); 
#>
</head>
<body>

<?php
  // code PHP
  echo 'hello world!';
?>

<%
  /* code ASP */
  response.write("Hello World!")
%>

<!-- Comments -->
Hello World
</body></html>
Outside HTML
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

get_nodes($tidy->html());

function get_nodes($node) {

    // Vérifie si le noeud courant est du type demandé
    if($node->{"isJste()) {
        echo "\n\n# Noeud jste #" . ++$GLOBALS['num'] . "\n";
        echo $node->value;
    }

    // Vérifie si le noeud courant a des enfants
    if($node->hasChildren()) {
        foreach($node->child as $child) {
            get_nodes($child);
        }
    }
}

?>


    &example.outputs;


# Noeud jste #1
<# 
  /* code JSTE */
  alert('Hello World'); 
#>
