  Exemple de serveur Yar


<?php

/* supposons que cette page est accessible via l'URL http://example.com/operator.php */

class Operator {

    /**
     * Ajout de deux opérandes
     * @param interge 
     * @return interge
     */
    public function add($a, $b) {
        return $this->_add($a, $b);
    }

    /**
     * Sub 
     */
    public function sub($a, $b) {
        return $a - $b;
    }

    /**
     * Mul
     */
    public function mul($a, $b) {
        return $a * $b;
    }

    /**
     * Les méthodes protégées ne seront pas exposées
     * @param interge 
     * @return interge
     */
    protected function _add($a, $b) {
        return $a + $b;
    }
}

$server = new Yar_Server(new Operator());
$server->handle();
?>





  Accès au serveur depuis un navigateur (requête GET)
