refentry.refsect1:
  &reftitle.examples;



    Aggiunta di un nuovo metodo a DOMElement per semplificare il codice


<?php

class myElement extends DOMElement {
   function appendElement($name) { 
      return $this->appendChild(new myElement($name));
   }
}

class myDocument extends DOMDocument {
   function setRoot($name) { 
      return $this->appendChild(new myElement($name));
   }
}

$doc = new myDocument();
$doc->registerNodeClass('DOMElement', 'myElement');

// D'ora in poi, aggiungere un elemento ad un altro costa solo una chiamata al metodo !
$root = $doc->setRoot('root');
$child = $root->appendElement('child');
$child->setAttribute('foo', 'bar');

echo $doc->saveXML();

?>


    &example.outputs;


<?xml version="1.0"?>
<root><child foo="bar"/></root>







    Recupero di elementi come classe personalizzata


<?php
class myElement extends DOMElement {
    public function __toString() {
        return $this->nodeValue;
    }
}

$doc = new DOMDocument;
$doc->loadXML("<root><element><child>text in child</child></element></root>");
$doc->registerNodeClass("DOMElement", "myElement");

$element = $doc->getElementsByTagName("child")->item(0);
var_dump(get_class($element));

// E approfitta del metodo __toString..
echo $element;
?>


    &example.outputs;


string(9) "myElement"
text in child







    Recupero del proprietario del documento


     Quando si crea un'istanza di un DOMDocument personalizzato, la
     proprietà ownerDocument farà riferimento alla classe
     istanziata, il che significa che non è necessario (e in realtà non è possibile) utilizzare
     DOMDocument::registerNodeClass con
     DOMDocument



<?php
class myDOMDocument extends DOMDocument {
}

class myOtherDOMDocument extends DOMDocument {
}

// Crea myDOMDocument con un po' di XML
$doc = new myDOMDocument;
$doc->loadXML("<root><element><child>text in child</child></element></root>");

$child = $doc->getElementsByTagName("child")->item(0);

// L'attuale proprietario del nodo è myDOMDocument
var_dump(get_class($child->ownerDocument));

// Importa un nodo da myDOMDocument
$newdoc = new myOtherDOMDocument;
$child = $newdoc->importNode($child);

// Il nuovo proprietario del nodo è cambiato in myOtherDOMDocument
var_dump(get_class($child->ownerDocument));
?>


    &example.outputs;


string(13) "myDOMDocument"
string(18) "myOtherDOMDocument"
