refentry.refsect1:
  &reftitle.examples;



    ftp_ssl_connect の例


<?php

// SSL 接続を確立する
$conn_id = ftp_ssl_connect($ftp_server);

// ユーザー名とパスワードでログインする
$login_result = ftp_login($conn_id, $ftp_user_name, $ftp_user_pass);

if (!$login_result) {
    // この場合は、すでに PHP 側で E_WARNING レベルのメッセージを発行しています。
    die("can't login");
}

echo ftp_pwd($conn_id); // /

// SSL 接続を閉じる
ftp_close($conn_id);
?>
