refentry.refsect1:
&reftitle.description;
stringftp_mkdir resourcestrumień_ftp stringkatalog
Tworzy podany katalog na serwerze FTP.

  &reftitle.examples;



    Przykład użycia ftp_mkdir


<?php

$katalog = 'www';

// nawiązanie połączenia
$conn_id = ftp_connect($ftp_server);

// logowanie
$login_result = ftp_login($conn_id, $ftp_user_name, $ftp_user_pass);

// próba stworzenia katalogu
if (ftp_mkdir($conn_id, $katalog)) {
 echo "Pomyślnie stworzono katalog $katalog\n";
} else {
 echo "Wystąpiły błędy przy tworzeniu katalogu $katalog\n";
}

// zamknięcie połączenia
ftp_close($conn_id);
?>
