refentry.refsect1:
  &reftitle.examples;



    Przykład użycia ftp_size


<?php

$plik = 'plik.txt';

// nawiązanie połączenia
$conn_id = ftp_connect($ftp_server);

// logowanie
$login_result = ftp_login($conn_id, $ftp_user_name, $ftp_user_pass);

// pobranie rozmiaru pliku $plik
$res = ftp_size($conn_id, $plik);

if ($res != -1) {
    echo "Rozmiar pliku $plik to $res bajtów";
} else {
    echo "Nie można pobrać wielkości pliku.";
}

// zamknięcie połączenia
ftp_close($conn_id);

?>
