refentry.refsect1:
&reftitle.description;
arrayimap_list resourceimap_stream stringref stringpattern
Читает список почтовых ящиков.

&reftitle.parameters;



  &imap.imap-stream.description;

   ref



     В ref, как правило, должна быть
     указана только спецификация сервера, как описано в
     imap_open.

    &imap.mailboxname.insecure;



   pattern

    &imap.pattern;

  &reftitle.examples;



    Пример использования imap_list


<?php
$mbox = imap_open("{imap.example.org}", "username", "password", OP_HALFOPEN)
      or die("не удалось подключиться: " . imap_last_error());

$list = imap_list($mbox, "{imap.example.org}", "*");
if (is_array($list)) {
    foreach ($list as $val) {
        echo imap_utf7_decode($val) . "\n";
    }
} else {
    echo "вызов imap_list завершился с ошибкой: " . imap_last_error() . "\n";
}

imap_close($mbox);
?>
