refentry.refsect1:
&reftitle.description;

 public boolPharFileInfo::isCompressedBZIP2


&phar.removed.pharfileinfocompress;



 Определяет, сжат ли файл в phar-архиве с помощью bzip2.

  &reftitle.examples;



    Пример использования PharFileInfo::isCompressedBZIP2


<?php
try {
    $p = new Phar('/path/to/my.phar', 0, 'my.phar');
    $p['myfile.txt'] = 'hi';
    $p['myfile2.txt'] = 'hi';
    $p['myfile3.txt'] = 'hi';
    $p['myfile2.txt']->setCompressedGZ();
    $p['myfile3.txt']->setCompressedBZIP2();
    $file = $p['myfile.txt'];
    $file2 = $p['myfile2.txt'];
    $file3 = $p['myfile3.txt'];
    var_dump($file->isCompressedBZIP2());
    var_dump($file2->isCompressedBZIP2());
    var_dump($file3->isCompressedBZIP2());
} catch (Exception $e) {
    echo 'Не удалось создать/изменить phar my.phar: ', $e;
}
?>


    &example.outputs;


bool(false)
bool(false)
bool(true)
