refentry.refsect1:
&reftitle.description;

 booltidyNode::isComment




 Düğümün bir açıklama düğümü olup olmadığına bakar.

  &reftitle.examples;



    - Karışık bir HTML belgeden açıklamaları çıkarmak


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>Başlık</title>'; ?>
<#
  /* JSTE kodu */
  alert('Merhaba Dünya');
#>
</head>
<body>

<?php
  // PHP code
  echo 'merhaba dünya!';
?>

<%
  /* ASP kodu */
  response.write("Merhaba Dünya!")
%>

<!-- Açıklamalar -->
Merhaba Dünya
</body></html>
HTML dışı
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

get_nodes($tidy->html(), 'comment');

function get_nodes($node, $node_type) {

    // Düğüm istenen türde mi?
    if($node->{"is$node_type"}()) {
        echo "\n\n# $node_type node #" . ++$GLOBALS['num'] . "\n";
        echo $node->value;
    }

    // Düğümün çocukları var mı?
    if($node->hasChildren()) {
        foreach($node->child as $child) {
            get_nodes($child, $node_type);
        }
    }
}

?>


    &example.outputs;


# jste node #1
<#
  /* JSTE todu */
  alert('Merhaba Dünya');
#>

